<?php/** * Created by PhpStorm. * User: Administrator * Date: 2017/9/22 * Time: 14:20 */namespace App\Services;use App\Models\AccountRecord;class AccountRecordService{    public $accountRecord;    public function __construct()    {        $this->accountRecord = new AccountRecord();    }    /**     * 设置账户记录     * @return bool     */    public function setAccountRecord($user_id,$num,$type,$info,$is_add,$class = 1)    {        $accountRecord = $this->accountRecord->create([            'user_id' => $user_id,            'num' => $num,            'type' => $type,            'info' => $info,            'is_add' => $is_add,            'class' => $class,        ]);        if(count($accountRecord) == 0)            return false;        return true;    }}