<?php/** * Created by PhpStorm. * User: Administrator * Date: 2017/9/22 * Time: 14:54 */namespace App\Services;use App\Models\ThreeRecord;class ThreeRecordService{    public $threeRecord;    public function __construct(ThreeRecord $threeRecord)    {        $this->threeRecord = $threeRecord;    }    public function setRecord($from_id, $to_id, $num, $info)    {        $threeRecord = $this->threeRecord->create([            'from_id' => $from_id,            'to_id' => $to_id,            'num' => $num,            'info' => $info,        ]);        if (count($threeRecord) == 0)            return false;        return true;    }}